$(document).ready(function() {
	var a = $('.dater'),
		b = $('.timer'),
		c = $('.start'),
		d = $('.wlow'),
		e = $('.main'),
		f = $('.menu'),
		g = $('a#cerita'),
		h = $('a#timeline'),
		i = $('a#unique'),
		j = $('a#gallery'),
		k = $('a#about'),
	   qa = "",
	   qt = "",
 interval = setInterval(function() {var x = moment();a.html(x.format('dddd')+', '+x.format('DD MMMM YYYY'));b.html(x.format('hh:mm:ss'));}, 100);
	c.click(function() {	
 	  e.animate({
 		 'background-position-x': '90%'
	  }, 200, 'linear');
	  d.animate({
 		 'right': '550px'
	  }, 200, 'linear');
	  c.animate({
 		 'opacity': 0
	  }, 100, 'linear');
	  setTimeout(function(){f.show();}, 300);
	  setTimeout(function(){g.show();
	    g.animate({
			'top': '0',
			'opacity': '1',
			'left': '150px'
		}, 200);
	  }, 400);
	  setTimeout(function(){h.show();
	    h.animate({
			'top': '50px',
			'opacity': '1',
			'left': '180px'
		}, 200);
	  }, 500);
	  setTimeout(function(){i.show();
	    i.animate({
			'top': '100px',
			'opacity': '1',
			'left': '210px'
		}, 200);
	  }, 600);
	  setTimeout(function(){j.show();
	    j.animate({
			'top': '150px',
			'opacity': '1',
			'left': '180px'
		}, 200);
	  }, 700);
	  setTimeout(function(){k.show();
	    k.animate({
			'top': '200px',
			'opacity': '1',
			'left': '150px'
		}, 200);
	  }, 800);
	});
	
	function getQuote() {

  $.getJSON("https://api.forismatic.com/api/1.0/?method=getQuote&key=457653&format=jsonp&lang=en&jsonp=?", function(data) {
    qt = data.quoteText;

    if (data.quoteAuthor) {
      qa = data.quoteAuthor;
    } else {
      qa = "Anonymous"
    }
    $('#quote').html(qt)
    $('.author').html(' &mdash; '+qa)
  }); 
}
getQuote();
});